<script>
export default {
  name: 'Wordmark',
  props: {
    text: String,
    pauseAnimation: Boolean,
  },
  data() {
    return {
      animationFunction: null,
      activeWordmark: 0,
    };
  },
  created() {
    this.animationFunction = setInterval(() => {
      if (this.pauseAnimation) return;
      const nextWordmark = this.activeWordmark + 1;
      this.activeWordmark = (nextWordmark < 3) ? nextWordmark : 0;
    }, 400);
  },
  destroyed() {
    window.clearInterval(this.animationFunction);
  },
};
</script>

<style scoped lang="scss">
@import '../../scss/colours';

.wordmark {
  text-align: center;
  color: $yellow;
  cursor: pointer;

  figure {
    display: block;
  }
}
